import { useEffect, useState } from "react"

const useFetch = (url) => {
    const [product, setProduct] = useState(null)

    useEffect(() => {
        fetch(url) //returns a promise
            .then(res => {
                return res.json() //also returns a promise
            })
            .then(data => {
                console.log(data)
                setProduct(data)
            })
    }, [url])

    return {product}
}
 
export default useFetch;
