import { useContext, useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { UserContext } from "./UserContext";

const Cart = () => {

    const {cartItems, setCartItems, value, setValue, cartItemsPrice, setCartItemsPrice} = useContext(UserContext);
    const [totalPayment, setTotalPayment] = useState(true)
    const [totalPaymentPrice, setTotalPaymentPrice] = useState(0)

    const history = useHistory()

    const clearItems = () => {
        setTotalPayment(false)
        setCartItems([])
        setValue(0)
    }
 
    useEffect( () => {
        if (totalPayment) {
            const total = cartItemsPrice.reduce((accumulator, currentValue) => accumulator + currentValue.price, 0)
            const totalPrice = total.toFixed(2)
            setTotalPaymentPrice(totalPrice)

            
        } else {

            setCartItemsPrice([])
            // console.log(cartItemsPrice)

            setTotalPaymentPrice(0)
            console.log("4 else totalPaymentPrice", totalPaymentPrice)

        }

        return (
            <div>{totalPaymentPrice}</div>
        )
        
    })

    // const ShowMoney = (meow) => {
    //     if (totalPayment) {
    //         meow = cartItemsPrice.reduce((accumulator, currentValue) => accumulator + currentValue.price, 0)
    //         const totalPrice = meow
    //         setTotalPaymentPrice(totalPrice)
    //         // .toFixed(2)
    //         // setTotalPaymentPrice(totalPrice)

    //         return (
    //             <div>{totalPaymentPrice}</div>
    //         )
    //     } else {
    //         meow = 0

    //         setCartItemsPrice([])

    //         // setTotalPaymentPrice(meow)
    //         console.log("4 else totalPaymentPrice", totalPaymentPrice)
    //         return (
    //             <div>{totalPaymentPrice}</div>
    //         )

    //     }
    // }
        
    return (
        <div className="cart-list">
            <h2>Cart</h2>

            <div className="cart-column-1">
                {cartItems.map( (item) => (
                    <div className="cart-preview" key={item.id}>

                        <div className="cart-image">
                            <img src={item.image} />

                            <div className="cart-details">
                                <h3>{item.title}</h3>
                                <p>Price: ${item.price}</p>
                            </div>
                        </div>

                    </div>
                ))}

            </div>

            <div className="cart-column-2">
                <h3>Total Items</h3>
                <p>{value}</p>

                <h3>Total Payment</h3>
                {/* <ShowTotalPrice/> */}
                <p>{totalPaymentPrice}</p>
                {/* <ShowMoney /> */}

                <button className="cart-btn-checkout" onClick={() => {

                    console.log("value init", totalPaymentPrice)
                    // setTotalPaymentPrice(0)
                    console.log("value after", totalPaymentPrice)
                    clearItems()
                    // history.push("/store")

                }}>Checkout</button>

                <button className="cart-btn-clear" onClick={clearItems}>Clear</button>
            </div>
        </div>
    );
}
 
export default Cart;
