import { useState } from 'react';
import {Link} from 'react-router-dom';

const Navbar = ({count}) => {

    const [counts, setCount] = useState(0)

    const counter = () => {
        const newCount = counts + 1
        setCount(newCount)
    }

    return ( 
        <nav className="navbar">
            <h1>ShopNow</h1>
            <div className="links">
                <Link to="/">Home</Link>
                <Link to="/cart">Cart ({counts})</Link>
                <Link to="/login" style={{
                    color: "white",
                    backgroundColor: "#f1356d",
                    borderRadius: "8px"
                }}>Login</Link>
            </div>

            <button onClick={counter}>add count</button>
        </nav>
    );
}
 
export default Navbar;
