import { useContext } from "react";
import { useParams } from "react-router";
import useFetch from "./useFetch";
import { UserContext } from "./UserContext";

const ProductDetails = () => {
    const {id} = useParams()
    const {product} = useFetch("https://fakestoreapi.com/products/" + id)

    const {value, setValue, cartItemsPrice, setCartItemsPrice} = useContext(UserContext);

    // const addCart = () => {
    //     const newCount = value + 1
    //     setValue(newCount)
    // }

    return (
        <div className="product-details">
            {product && (
                <div className="product-details-preview">

                    <div className="image-column">
                        <img src={product.image} alt={`Product ${product.id}`} />
                    </div>

                    <div className="details-column">
                        <h3>{product.title}</h3>
                        <p className="details-category">Category: {product.category}</p>
                        <p className="details-price">$ {product.price}</p>
                        <p className="details-description">{product.description}</p>
                        <button onClick={() => {
                            setValue(value + 1)

                            setCartItemsPrice([...cartItemsPrice, {
                                id: cartItemsPrice.length,
                                price: product.price
                            }])

                        }}>Add to Cart</button>
                    </div>
                </div>
            )}
        </div>
    );
}
 
export default ProductDetails;
