import { useEffect, useState } from "react";

const Home = () => {

    const [count, setCount] = useState()
    const [jokes, setJokes] = useState()
    const [loading, setLoading] = useState(true)


    useEffect(() => {
        fetch("https://api.chucknorris.io/jokes/random") //returns a promise
            .then(res => {
                return res.json() //also returns a promise
            })
            .then(data => {
                console.log(data)
                setJokes(data)
                setLoading(false)
            })
    }, [jokes])

    return ( 
        <div className="home">
            <h1>Counter</h1>
            <p>{count}</p>
            <button onClick={() => setCount(jokes.value)}>Show Joke</button>
        </div>
    );
}
 
export default Home;
