import useFetch from "./useFetch";
import ProductList from "./ProductList";
import { useEffect, useState } from "react";

const Home = () => {

    // const {product} = useFetch("https://fakestoreapi.com/products/")

    const [product, setProduct] = useState(null)
    

    useEffect(() => {
        fetch("https://fakestoreapi.com/products/")
            .then(res => {
                return res.json()
            })
            .then(data => {
                console.log(data)
                setProduct(data)
            })
    }, [])

    const filterProduct = () => {
        const newProduct = product.filter(products => product.id !== "jewelery")
        setProduct(newProduct)
    }

    

    return ( 
        <div className="home">
            <button onSubmit={filterProduct}>Men's Clothing</button>
            {product && <ProductList products={product} title="Products of the day!" />}
        </div>
    );
}
 
export default Home;
