import { useContext } from "react";
import { UserContext } from "./UserContext";

const Cart = () => {

    const {cartItems, setCartItems} = useContext(UserContext);

    return (
        <div className>
            <h2>Cart</h2>
            {cartItems.map( (item) => (
                <div className="cart-items" key={item.id}>
                    <h3>
                        {item.title}
                    </h3>
                    <p>{item.price}</p>

                </div>
            ))}
        </div>
    );
}
 
export default Cart;
