import useFetch from "./useFetch";
import ProductList from "./ProductList";

const Home = () => {

    const {product, setProduct} = useFetch("https://fakestoreapi.com/products/")

    const filterProduct = () => {
        const newProduct = product.filter(products => products.category === "men's clothing")
        setProduct(newProduct)
        console.log(product)
    }

    return ( 
        <div className="home">
            {product && <ProductList products={product} title="Products of the day!" filterProduct={filterProduct}/>}
        </div>
    );
}
 
export default Home;
