import { useParams } from "react-router";
import useFetch from "./useFetch";
useFetch

const ProductDetails = () => {
    const {id} = useParams()
    const {product} = useFetch("https://fakestoreapi.com/products/" + id)

    return (
        <div className="product-details">
            {product && (
                <div>
                    <h3>{product.title}</h3>
                </div>
            )}
        </div>

    );
}
 
export default ProductDetails;
