import { UserContext } from "./UserContext";

const Cart = () => {

    const {cartItems, setCartItems, value, setValue, cartItemsPrice, setCartItemsPrice} = useContext(UserContext);
    

    class TravellerCollection extends Array {
        sum(key) {
            return this.reduce((a, b) => a + (b[key] || 0), 0);
        }
    }

    
    function addPrice(key) {
        cartItemsPrice
    }

    const traveler = new TravellerCollection(cartItemsPrice)



    return (
        <div className="cart-list">
            <h2>Cart</h2>

            <div className="cart-column-1">
                {cartItems.map( (item) => (
                    <div className="cart-preview" key={item.id}>

                        <div className="cart-image">
                            <img src={item.image} />

                            <div className="cart-details">
                                <h3>{item.title}</h3>
                                <p>Price: ${item.price}</p>
                            </div>
                        </div>

                    </div>
                ))}

            </div>

            <div className="cart-column-2">
                <h3>Total Items</h3>
                <p>15</p>
                <h3>Total Payment</h3>
                <div>
                    {/* {cartItemsPrice.map( (price) => (
                        <div key={price.id}> 
                            <p>{price.price}</p>
                        </div>

                    ))} */}

                
                </div>
                <button>CHECKOUT</button>
                <button className="cart-btn-clear" onClick={() => {

                    
                    console.log("traveler array", traveler)
                    console.log("traveler array 0", traveler[0])
                    console.log("traveler array sum", traveler[0].sum("price"))

                    console.log("price show", cartItemsPrice)

                    setCartItems([])
                    console.log(cartItems)
                    setValue(0)
                    console.log(value)
                }}>CLEAR</button>
            </div>
        </div>
    );
}
 
export default Cart;
