import { useParams } from "react-router";
import useFetch from "./useFetch";

const ProductDetails = () => {
    const {id} = useParams()
    const {product} = useFetch("http://localhost:3001/" + id)

    return (
        <div className="product-details">
            <p>Product {id}</p>
            {product && (
                <div>
                    <h3>{product.title}</h3>
                </div>
            )}
        </div>

    );
}
 
export default ProductDetails;
