import { useEffect, useState } from "react";
import JokeList from "./JokeList";

const Home = () => {

    const [count, setCount] = useState()
    const [jokes, setJokes] = useState()
    const [loading, setLoading] = useState(true)


    useEffect(() => {
        fetch("https://api.chucknorris.io/jokes/random") //returns a promise
            .then(res => {
                return res.json() //also returns a promise
            })
            .then(data => {
                console.log(data)
                setJokes(data)
                setLoading(false)
            })
    }, [jokes])

    return ( 
        <div className="home">
            <JokeList blogs={jokes} title="All Jokes!" />
        </div>
    );
}
 
export default Home;
