import { useContext, useEffect, useState } from "react";
import { UserContext } from "./UserContext";

const Cart = () => {

    const {cartItems, setCartItems, value, setValue, cartItemsPrice, setCartItemsPrice} = useContext(UserContext);

    const [totalPayment, setTotalPayment] = useState(true)

    function ShowTotalPrice() {
        if (totalPayment) {
            const result = cartItemsPrice.reduce((accumulator, currentValue) => accumulator + currentValue.price, 0)
            
            return (
                <p>{result}</p>
            )
        } else {
            const result = 0

            return (
                <p>{result}</p>
            )
        }
 
    }
        

    return (
        <div className="cart-list">
            <h2>Cart</h2>

            <div className="cart-column-1">
                {cartItems.map( (item) => (
                    <div className="cart-preview" key={item.id}>

                        <div className="cart-image">
                            <img src={item.image} />

                            <div className="cart-details">
                                <h3>{item.title}</h3>
                                <p>Price: ${item.price}</p>
                            </div>
                        </div>

                    </div>
                ))}

            </div>

            <div className="cart-column-2">
                <h3>Total Items</h3>
                <p>15</p>
                <h3>Total Payment</h3>
                <ShowTotalPrice />
                <button>CHECKOUT</button>
                <button className="cart-btn-clear" onClick={() => {

                    setTotalPayment(false)

                    // console.log("cart items total price", result)

                    console.log("price show", cartItemsPrice)

                    setCartItems([])
                    console.log(cartItems)
                    setValue(0)
                    console.log(value)
                }}>CLEAR</button>
            </div>
        </div>
    );
}
 
export default Cart;
